import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate(); // Use the navigate function to redirect the user

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            // Send login request to the backend
            const response = await axios.post('https://localhost:5133/api/login', {
                UserName: username,
                Password: password
            });

            // On successful login, save the access token in localStorage or sessionStorage
            localStorage.setItem('accessToken', response.data.accessToken);
            // Redirect to the App page after successful login
            navigate('/');
        } catch (error) {
            // Handle error if login fails
            setErrorMessage('Login failed: Invalid username or password.');
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            {errorMessage && <div className="error-message">{errorMessage}</div>}
            <form onSubmit={handleLogin}>
                <div className="input-group">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
