import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';
import Header from './Components/Header';
import Footer from './Components/Footer';
import Modal from './Components/Modal';
import { Route, Routes, useNavigate } from 'react-router-dom';
import Login from './Pages/Login'; // Import the Login component

const axiosInstance = axios.create({
    baseURL: 'https://localhost:5133/api',
    headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
        'Content-Type': 'application/json',
    }
});

const App = () => {
    const [movies, setMovies] = useState([]);
    const [expandedMovieId, setExpandedMovieId] = useState(null);
    const [modalVisible, setModalVisible] = useState(false);
    const [posts, setPosts] = useState({});
    const [comments, setComments] = useState({});
    const [newMovie, setNewMovie] = useState({ title: '', description: '' });
    const [newPost, setNewPost] = useState({ title: '', body: '' });
    const [newComment, setNewComment] = useState({ content: '' });

    const navigate = useNavigate();
    const isLoggedIn = localStorage.getItem('accessToken') !== null;

    useEffect(() => {
        const loadMovies = async () => {
            try {
                const response = await axiosInstance.get('/movies');
                setMovies(response.data);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        if (isLoggedIn) {
            loadMovies();
        }
    }, [isLoggedIn]);

    if (!isLoggedIn) {
        return <Login />;
    }

    const handleLogout = () => {
        localStorage.removeItem('accessToken');
        navigate('/login');
    };

    const MovieList = () => {
        const loadPosts = async (movieId) => {
            try {
                const response = await axiosInstance.get(`/movies/${movieId}/posts`);
                setPosts((prev) => ({ ...prev, [movieId]: response.data }));
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        const loadComments = async (movieId, postId) => {
            try {
                const response = await axiosInstance.get(`/movies/${movieId}/posts/${postId}/comments`);
                setComments((prev) => ({
                    ...prev,
                    [postId]: response.data,
                }));
            } catch (error) {
                console.error('Error fetching comments:', error);
            }
        };

        const handleCreateMovie = async (e) => {
            e.preventDefault();
            try {
                const response = await axiosInstance.post('/movies', newMovie);
                setMovies((prev) => [...prev, response.data]);
                setNewMovie({ title: '', description: '' });
            } catch (error) {
                console.error('Error creating movie:', error);
            }
        };

        const handleCreatePost = async (movieId, e) => {
            e.preventDefault();
            try {
                const response = await axiosInstance.post(`/movies/${movieId}/posts`, newPost);
                setPosts((prev) => ({
                    ...prev,
                    [movieId]: [...(prev[movieId] || []), response.data],
                }));
                setNewPost({ title: '', body: '' });
            } catch (error) {
                console.error('Error creating post:', error);
            }
        };

        const handleCreateComment = async (movieId, postId, e) => {
            e.preventDefault();
            try {
                const response = await axiosInstance.post(
                    `/movies/${movieId}/posts/${postId}/comments`,
                    newComment
                );
                setComments((prev) => ({
                    ...prev,
                    [postId]: [...(prev[postId] || []), response.data],
                }));
                setNewComment({ content: '' });
            } catch (error) {
                console.error('Error creating comment:', error);
            }
        };

        // Ensure minimal re-render by using useCallback
        const handleInputChange = useCallback((setter, state) => (e) => {
            setter({ ...state, [e.target.name]: e.target.value });
        }, []);

        return (
            <div className="App">
                <Header />
                <div className="content">
                    <h1>Movies List</h1>
                    <button onClick={handleLogout}>Logout</button>

                    <form onSubmit={handleCreateMovie} className="movie-form">
                        <h2>Create New Movie</h2>
                        <input
                            type="text"
                            name="title"
                            placeholder="Movie Title"
                            value={newMovie.title}
                            onChange={handleInputChange(setNewMovie, newMovie)}
                        />
                        <textarea
                            name="description"
                            placeholder="Movie Description"
                            value={newMovie.description}
                            onChange={handleInputChange(setNewMovie, newMovie)}
                        ></textarea>
                        <button type="submit">Add Movie</button>
                    </form>

                    {movies.length > 0 ? (
                        <div className="movie-list">
                            {movies.map((movie) => (
                                <div className="movie-item" key={movie.id}>
                                    <h2>{movie.title}</h2>
                                    <p>{movie.description}</p>
                                    <button
                                        onClick={() => {
                                            setExpandedMovieId((prev) =>
                                                prev === movie.id ? null : movie.id
                                            );
                                            loadPosts(movie.id);
                                        }}
                                    >
                                        {expandedMovieId === movie.id ? 'Hide Details' : 'Show Posts'}
                                    </button>
                                    {expandedMovieId === movie.id && posts[movie.id] && (
                                        <div className="posts">
                                            <h3>Posts:</h3>
                                            <form onSubmit={(e) => handleCreatePost(movie.id, e)}>
                                                <input
                                                    type="text"
                                                    name="title"
                                                    placeholder="Post Title"
                                                    value={newPost.title}
                                                    onChange={handleInputChange(setNewPost, newPost)}
                                                />
                                                <textarea
                                                    name="body"
                                                    placeholder="Post Body"
                                                    value={newPost.body}
                                                    onChange={handleInputChange(setNewPost, newPost)}
                                                ></textarea>
                                                <button type="submit">Add Post</button>
                                            </form>
                                            {posts[movie.id].length > 0 ? (
                                                posts[movie.id].map((post) => (
                                                    <div className="post-item" key={post.id}>
                                                        <h4>{post.title}</h4>
                                                        <p>{post.body}</p>
                                                        <button
                                                            onClick={() => loadComments(movie.id, post.id)}
                                                        >
                                                            Show Comments
                                                        </button>
                                                        {comments[post.id] && (
                                                            <div className="comments">
                                                                <h5>Comments:</h5>
                                                                <form
                                                                    onSubmit={(e) =>
                                                                        handleCreateComment(movie.id, post.id, e)
                                                                    }
                                                                >
                                                                    <input
                                                                        type="text"
                                                                        name="content"
                                                                        placeholder="Comment Content"
                                                                        value={newComment.content}
                                                                        onChange={handleInputChange(setNewComment, newComment)}
                                                                    />
                                                                    <button type="submit">Add Comment</button>
                                                                </form>
                                                                {comments[post.id].length > 0 ? (
                                                                    comments[post.id].map((comment) => (
                                                                        <p key={comment.id}>{comment.content}</p>
                                                                    ))
                                                                ) : (
                                                                    <p>No comments available.</p>
                                                                )}
                                                            </div>
                                                        )}
                                                    </div>
                                                ))
                                            ) : (
                                                <p>No posts available.</p>
                                            )}
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p>Loading movies...</p>
                    )}
                </div>
                {modalVisible && <Modal />}
                <Footer />
            </div>
        );
    };

    return (
        <Routes>
            <Route path="/" element={<MovieList />} />
            <Route path="/login" element={<Login />} />
        </Routes>
    );
};

export default App;
