import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import './App.css';
import Header from './Components/Header';
import Footer from './Components/Footer';
import Modal from './Components/Modal';
import { Route, Routes, useNavigate } from 'react-router-dom';
import Login from './Pages/Login';

const axiosInstance = axios.create({
    baseURL: 'https://localhost:5133/api',
    headers: {
        Authorization: `Bearer ${localStorage.getItem('accessToken')}`,
        'Content-Type': 'application/json',
    },
});

const App = () => {
    const [movies, setMovies] = useState([]);
    const [expandedMovieId, setExpandedMovieId] = useState(null);
    const [modalVisible, setModalVisible] = useState(false);
    const [posts, setPosts] = useState({});
    const [comments, setComments] = useState({});
    const [editingMovie, setEditingMovie] = useState(null);

    const navigate = useNavigate();
    const isLoggedIn = localStorage.getItem('accessToken') !== null;

    const movieTitleRef = useRef(null);
    const movieDescriptionRef = useRef(null);
    const postTitleRef = useRef(null);
    const postBodyRef = useRef(null);
    const commentContentRef = useRef(null);

    const userID = isLoggedIn ? jwtDecode(localStorage.getItem('accessToken')).sub : null;

    useEffect(() => {
        const loadMovies = async () => {
            try {
                const response = await axiosInstance.get('/movies');
                setMovies(response.data);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        if (isLoggedIn) {
            loadMovies();
        }
    }, [isLoggedIn]);

    if (!isLoggedIn) {
        return <Login />;
    }

    const handleLogout = () => {
        localStorage.removeItem('accessToken');
        navigate('/login');
    };

    const handleCreateMovie = async (e) => {
      e.preventDefault();
      try {
          const newMovie = {
              title: movieTitleRef.current.value,
              description: movieDescriptionRef.current.value,
          };
          const response = await axiosInstance.post('/movies', newMovie);
          setMovies((prev) => [...prev, response.data]);
          setModalVisible(false);
      } catch (error) {
          console.error('Error creating movie:', error);
      }
  };


    const handleEditMovie = (movie) => {
        setEditingMovie(movie);
        setModalVisible(true);
    };



    const handleUpdateMovie = async (e) => {
      e.preventDefault();

      try {
          const updatedMovie = {
              title: movieTitleRef.current.value,
              description: movieDescriptionRef.current.value,
          };

          await axiosInstance.put(`/movies/${editingMovieId}`, updatedMovie);

          setMovies((prev) =>
              prev.map((movie) =>
                  movie.id === editingMovieId
                      ? { ...movie, ...updatedMovie }
                      : movie
              )
          );

          setEditingMovieId(null); // Clear editing state
          setModalVisible(false); // Close modal
      } catch (error) {
          console.error('Error updating movie:', error.response?.data || error.message);
      }
  };

    const handleDeleteMovie = async (movieId) => {
        try {
            await axiosInstance.delete(`/movies/${movieId}`);
            setMovies((prev) => prev.filter((movie) => movie.id !== movieId));
        } catch (error) {
            console.error('Error deleting movie:', error);
        }
    };

    const MovieList = () => {
        const loadPosts = async (movieId) => {
            try {
                const response = await axiosInstance.get(`/movies/${movieId}/posts`);
                setPosts((prev) => ({ ...prev, [movieId]: response.data }));
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        const loadComments = async (movieId, postId) => {
            try {
                const response = await axiosInstance.get(`/movies/${movieId}/posts/${postId}/comments`);
                setComments((prev) => ({
                    ...prev,
                    [postId]: response.data,
                }));
            } catch (error) {
                console.error('Error fetching comments:', error);
            }
        };

        const handleDeletePost = async (movieId, postId) => {
            try {
                await axiosInstance.delete(`/movies/${movieId}/posts/${postId}`);
                setPosts((prev) => ({
                    ...prev,
                    [movieId]: prev[movieId].filter((post) => post.id !== postId),
                }));
            } catch (error) {
                console.error('Error deleting post:', error);
            }
        };

        const handleDeleteComment = async (postId, commentId) => {
            try {
                await axiosInstance.delete(`/movies/posts/${postId}/comments/${commentId}`);
                setComments((prev) => ({
                    ...prev,
                    [postId]: prev[postId].filter((comment) => comment.id !== commentId),
                }));
            } catch (error) {
                console.error('Error deleting comment:', error);
            }
        };

        return (
            <div className="App">
                <Header />
                <div className="content">
                    <h1>Movies List</h1>
                    <button onClick={handleLogout} className="logout-btn">Logout</button>
                    <button onClick={() => setModalVisible(true)} className="create-movie-btn">Create New Movie</button>

                    {modalVisible && (
                        <Modal>
                            <h2>{editingMovie ? 'Edit Movie' : 'Create New Movie'}</h2>
                            <form onSubmit={editingMovie ? handleUpdateMovie : handleCreateMovie}>
                                <input
                                    type="text"
                                    ref={movieTitleRef}
                                    defaultValue={editingMovie?.title || ''}
                                    placeholder="Movie Title"
                                    required
                                />
                                <textarea
                                    ref={movieDescriptionRef}
                                    defaultValue={editingMovie?.description || ''}
                                    placeholder="Movie Description"
                                    required
                                ></textarea>
                                <button type="submit">{editingMovie ? 'Update Movie' : 'Add Movie'}</button>
                                <button
                                    type="button"
                                    onClick={() => {
                                        setModalVisible(false);
                                        setEditingMovie(null);
                                    }}
                                    className="close-modal-btn"
                                >
                                    Close
                                </button>
                            </form>
                        </Modal>
                    )}

                    {movies.length > 0 ? (
                        <div className="movie-list">
                            {movies.map((movie) => (
                                <div className="movie-item" key={movie.id}>
                                    <h2>{movie.title}</h2>
                                    <p>{movie.description}</p>
                                    <button onClick={() => handleEditMovie(movie)}>Edit</button>
                                    <button onClick={() => handleDeleteMovie(movie.id)}>Delete</button>
                                    <button
                                        onClick={() => {
                                            setExpandedMovieId((prev) =>
                                                prev === movie.id ? null : movie.id
                                            );
                                            loadPosts(movie.id);
                                        }}
                                    >
                                        {expandedMovieId === movie.id ? 'Hide Details' : 'Show Posts'}
                                    </button>
                                    {expandedMovieId === movie.id && posts[movie.id] && (
                                        <div className="posts">
                                            <h3>Posts:</h3>
                                            {posts[movie.id].map((post) => (
                                                <div className="post-item" key={post.id}>
                                                    <h4>{post.title}</h4>
                                                    <p>{post.body}</p>
                                                    <button onClick={() => handleDeletePost(movie.id, post.id)}>Delete Post</button>
                                                    <button onClick={() => loadComments(movie.id, post.id)}>Show Comments</button>
                                                    {comments[post.id] && (
                                                        <div className="comments">
                                                            <h5>Comments:</h5>
                                                            {comments[post.id].map((comment) => (
                                                                <div key={comment.id}>
                                                                    <p>{comment.content}</p>
                                                                    <button onClick={() => handleDeleteComment(post.id, comment.id)}>Delete Comment</button>
                                                                </div>
                                                            ))}
                                                        </div>
                                                    )}
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p>Loading movies...</p>
                    )}
                </div>
                <Footer />
            </div>
        );
    };

    return (
        <Routes>
            <Route path="/" element={<MovieList />} />
            <Route path="/login" element={<Login />} />
        </Routes>
    );
};

export default App;
