import './Login.css';  // Import the CSS file
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // Send the username and password in the request body
            const response = await axios.post('https://localhost:5133/api/login', { userName: username, password });
            
            // Store the access token in localStorage
            localStorage.setItem('accessToken', response.data.accessToken);

            // Redirect to the home page after successful login
            navigate('/');
        } catch (error) {
            console.error('Login error', error);
        }
    };

    const handleRegister = () => {
        // Navigate to the register page programmatically
        navigate('/register');
    };

    return (
        <div className="login-container">
            <h1>Film Analysis Lounge</h1> {/* Title for the project */}
            <form className="login-form" onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Login</button>
            </form>

            {/* Button to navigate to the Register page */}
            <p>Don't have an account? <button onClick={handleRegister}>Register here</button></p>
        </div>
    );
};

export default Login;
