import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Užklausa login API
            const response = await axios.post('http://localhost:5133/api/login', {
                UserName: username,
                Password: password,
            });

            // Užtikrinsime, kad succesful login galėtų perkelti į kitą puslapį
            localStorage.setItem('accessToken', response.data.accessToken);  // Nors pageidautina naudoti sessionStorage ar cookies

            // Po prisijungimo, nukreipiame vartotoją į filmo puslapį
            navigate('/movies');
        } catch (err) {
            setError('Neteisingas vartotojo vardas arba slaptažodis');
        }
    };

    return (
        <div>
            <h2>Prisijungti</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="username">Vartotojo vardas:</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>
                <div>
                    <label htmlFor="password">Slaptažodis:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
                <button type="submit">Prisijungti</button>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </form>
        </div>
    );
};

export default Login;
