import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Header from './Components/Header';
import Footer from './Components/Footer';
import Modal from './Components/Modal';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Login from './login'; // Import the Login component

const App = () => {
    // State for handling movie list, posts, comments, and form data
    const [movies, setMovies] = useState([]);
    const [expandedMovieId, setExpandedMovieId] = useState(null);
    const [modalVisible, setModalVisible] = useState(false);
    const [posts, setPosts] = useState({});
    const [comments, setComments] = useState({});
    const [newMovie, setNewMovie] = useState({ title: '', description: '' });
    const [newPost, setNewPost] = useState({ title: '', body: '' });
    const [newComment, setNewComment] = useState({ content: '' });

    // Check if the user is logged in by looking for an access token in localStorage
    const isLoggedIn = localStorage.getItem('accessToken') !== null;

    // Load movies when the user is logged in
    useEffect(() => {
        const loadMovies = async () => {
            try {
                const response = await axios.get('https://localhost:5133/api/movies');
                setMovies(response.data);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        if (isLoggedIn) {
            loadMovies();
        }
    }, [isLoggedIn]);

    // If not logged in, redirect to login page
    if (!isLoggedIn) {
        return <Login />; // Redirect to login page if not logged in
    }

    // Movie List component (rendered after successful login)
    const MovieList = () => {
        // Load posts for a specific movie
        const loadPosts = async (movieId) => {
            try {
                const response = await axios.get(`https://localhost:5133/api/movies/${movieId}/posts`);
                setPosts((prev) => ({ ...prev, [movieId]: response.data }));
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        // Load comments for a specific post
        const loadComments = async (movieId, postId) => {
            try {
                const response = await axios.get(`https://localhost:5133/api/movies/${movieId}/posts/${postId}/comments`);
                setComments((prev) => ({
                    ...prev,
                    [postId]: response.data,
                }));
            } catch (error) {
                console.error('Error fetching comments:', error);
            }
        };

        // Handle creating a new movie
        const handleCreateMovie = async (e) => {
            e.preventDefault();
            try {
                const response = await axios.post('https://localhost:5133/api/movies', newMovie);
                setMovies((prev) => [...prev, response.data]); // Update movies state
                setNewMovie({ title: '', description: '' }); // Clear the form
            } catch (error) {
                console.error('Error creating movie:', error);
            }
        };

        // Handle creating a new post
        const handleCreatePost = async (movieId, e) => {
            e.preventDefault();
            try {
                const response = await axios.post(`https://localhost:5133/api/movies/${movieId}/posts`, newPost);
                setPosts((prev) => ({
                    ...prev,
                    [movieId]: [...(prev[movieId] || []), response.data],
                })); // Update posts state
                setNewPost({ title: '', body: '' }); // Clear the form
            } catch (error) {
                console.error('Error creating post:', error);
            }
        };

        // Handle creating a new comment
        const handleCreateComment = async (movieId, postId, e) => {
            e.preventDefault();
            try {
                const response = await axios.post(
                    `https://localhost:5133/api/movies/${movieId}/posts/${postId}/comments`,
                    newComment
                );
                setComments((prev) => ({
                    ...prev,
                    [postId]: [...(prev[postId] || []), response.data],
                })); // Update comments state
                setNewComment({ content: '' }); // Clear the form
            } catch (error) {
                console.error('Error creating comment:', error);
            }
        };

        return (
            <div className="App">
                <Header />
                <div className="content">
                    <h1>Movies List</h1>

                    {/* Create a new movie */}
                    <form onSubmit={handleCreateMovie} className="movie-form">
                        <h2>Create New Movie</h2>
                        <input
                            type="text"
                            placeholder="Movie Title"
                            value={newMovie.title}
                            onChange={(e) => setNewMovie({ ...newMovie, title: e.target.value })}
                        />
                        <textarea
                            placeholder="Movie Description"
                            value={newMovie.description}
                            onChange={(e) => setNewMovie({ ...newMovie, description: e.target.value })}
                        ></textarea>
                        <button type="submit">Add Movie</button>
                    </form>

                    {movies.length > 0 ? (
                        <div className="movie-list">
                            {movies.map((movie) => (
                                <div className="movie-item" key={movie.id}>
                                    <h2>{movie.title}</h2>
                                    <p>{movie.description}</p>
                                    <button
                                        onClick={() => {
                                            setExpandedMovieId((prev) =>
                                                prev === movie.id ? null : movie.id
                                            );
                                            loadPosts(movie.id);
                                        }}
                                    >
                                        {expandedMovieId === movie.id ? 'Hide Details' : 'Show Posts'}
                                    </button>
                                    {expandedMovieId === movie.id && posts[movie.id] && (
                                        <div className="posts">
                                            <h3>Posts:</h3>
                                            <form onSubmit={(e) => handleCreatePost(movie.id, e)}>
                                                <input
                                                    type="text"
                                                    placeholder="Post Title"
                                                    value={newPost.title}
                                                    onChange={(e) =>
                                                        setNewPost({ ...newPost, title: e.target.value })
                                                    }
                                                />
                                                <textarea
                                                    placeholder="Post Body"
                                                    value={newPost.body}
                                                    onChange={(e) =>
                                                        setNewPost({ ...newPost, body: e.target.value })
                                                    }
                                                ></textarea>
                                                <button type="submit">Add Post</button>
                                            </form>
                                            {posts[movie.id].length > 0 ? (
                                                posts[movie.id].map((post) => (
                                                    <div className="post-item" key={post.id}>
                                                        <h4>{post.title}</h4>
                                                        <p>{post.body}</p>
                                                        <button
                                                            onClick={() => loadComments(movie.id, post.id)}
                                                        >
                                                            Show Comments
                                                        </button>
                                                        {comments[post.id] && (
                                                            <div className="comments">
                                                                <h5>Comments:</h5>
                                                                <form
                                                                    onSubmit={(e) =>
                                                                        handleCreateComment(movie.id, post.id, e)
                                                                    }
                                                                >
                                                                    <input
                                                                        type="text"
                                                                        placeholder="Comment Content"
                                                                        value={newComment.content}
                                                                        onChange={(e) =>
                                                                            setNewComment({ ...newComment, content: e.target.value })
                                                                        }
                                                                    />
                                                                    <button type="submit">Add Comment</button>
                                                                </form>
                                                                {comments[post.id].length > 0 ? (
                                                                    comments[post.id].map((comment) => (
                                                                        <p key={comment.id}>{comment.content}</p>
                                                                    ))
                                                                ) : (
                                                                    <p>No comments available.</p>
                                                                )}
                                                            </div>
                                                        )}
                                                    </div>
                                                ))
                                            ) : (
                                                <p>No posts available.</p>
                                            )}
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p>Loading movies...</p>
                    )}
                </div>
                {modalVisible && <Modal />}
                <Footer />
            </div>
        );
    };

    return (
        <Router>
            <Routes>
                <Route path="/" element={<MovieList />} />
                <Route path="/login" element={<Login />} />
            </Routes>
        </Router>
    );
};

export default App;
