import { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Header from './Components/Header';
import Footer from './Components/Footer';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [expandedMovieId, setExpandedMovieId] = useState(null);
    const [posts, setPosts] = useState({});
    const [comments, setComments] = useState({});
    const [newMovie, setNewMovie] = useState({ title: '', description: '' });
    const [newPost, setNewPost] = useState({ title: '', body: '' });
    const [newComment, setNewComment] = useState({ content: '' });
    const [token, setToken] = useState(localStorage.getItem('token') || '');
    const [loginData, setLoginData] = useState({ username: '', password: '' });
    const [isLoading, setIsLoading] = useState(false);
    const [loginError, setLoginError] = useState(false);

    // Configure Axios with the token
    const apiClient = axios.create({
        baseURL: 'https://localhost:5133/api',
        headers: {
            Authorization: `Bearer ${token}`,
        },
    });

    // Fetch movies if a token exists
    useEffect(() => {
        if (token) {
            loadMovies();
        }
    }, [token]); // Trigger loading movies when token changes

    const loadMovies = async () => {
        try {
            const response = await apiClient.get('/movies');
            setMovies(response.data);
        } catch (error) {
            console.error('Error fetching movies:', error);
        }
    };

    const loadPosts = async (movieId) => {
        try {
            const response = await apiClient.get(`/movies/${movieId}/posts`);
            setPosts((prev) => ({ ...prev, [movieId]: response.data }));
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };

    const loadComments = async (movieId, postId) => {
        try {
            const response = await apiClient.get(`/movies/${movieId}/posts/${postId}/comments`);
            setComments((prev) => ({
                ...prev,
                [postId]: response.data,
            }));
        } catch (error) {
            console.error('Error fetching comments:', error);
        }
    };

    const handleCreateMovie = async (e) => {
        e.preventDefault();
        try {
            const response = await apiClient.post('/movies', newMovie);
            setMovies((prev) => [...prev, response.data]);
            setNewMovie({ title: '', description: '' });
        } catch (error) {
            console.error('Error creating movie:', error);
        }
    };

    const handleCreatePost = async (movieId, e) => {
        e.preventDefault();
        try {
            const response = await apiClient.post(`/movies/${movieId}/posts`, newPost);
            setPosts((prev) => ({
                ...prev,
                [movieId]: [...(prev[movieId] || []), response.data],
            }));
            setNewPost({ title: '', body: '' });
        } catch (error) {
            console.error('Error creating post:', error);
        }
    };

    const handleCreateComment = async (movieId, postId, e) => {
        e.preventDefault();
        try {
            const response = await apiClient.post(
                `/movies/${movieId}/posts/${postId}/comments`,
                newComment
            );
            setComments((prev) => ({
                ...prev,
                [postId]: [...(prev[postId] || []), response.data],
            }));
            setNewComment({ content: '' });
        } catch (error) {
            console.error('Error creating comment:', error);
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setLoginError(false);

        try {
            const response = await axios.post('https://localhost:5133/api/login', loginData);

            if (response.status === 200) {
                const userToken = response.data.token;
                setToken(userToken);
                localStorage.setItem('token', userToken); // Save the token to localStorage
                setLoginData({ username: '', password: '' });
                setLoginError(false); // Reset any previous errors
            } else {
                setLoginError(true);
            }
        } catch (error) {
            console.error('Error logging in:', error);
            setLoginError(true);
        } finally {
            setIsLoading(false);
        }
    };

    const handleLogout = () => {
        setToken('');
        localStorage.removeItem('token');
        setMovies([]);
        setPosts({});
        setComments({});
    };

    return (
        <div className="App">
            <Header />
            {loginError || !token ? (
                <form onSubmit={handleLogin} className="login-form">
                    <h2>Login</h2>
                    <input
                        type="text"
                        placeholder="Username"
                        value={loginData.username}
                        onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={loginData.password}
                        onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                    />
                    <button type="submit">Login</button>
                    {loginError && <p className="error">Login failed. Please try again.</p>}
                </form>
            ) : isLoading ? (
                <p>Loading movies...</p>
            ) : (
                <>
                    <button onClick={handleLogout}>Logout</button>
                    <div className="content">
                        <h1>Movies List</h1>
                        <form onSubmit={handleCreateMovie} className="movie-form">
                            <h2>Create New Movie</h2>
                            <input
                                type="text"
                                placeholder="Movie Title"
                                value={newMovie.title}
                                onChange={(e) => setNewMovie({ ...newMovie, title: e.target.value })}
                            />
                            <textarea
                                placeholder="Movie Description"
                                value={newMovie.description}
                                onChange={(e) =>
                                    setNewMovie({ ...newMovie, description: e.target.value })
                                }
                            ></textarea>
                            <button type="submit">Add Movie</button>
                        </form>

                        {movies.length > 0 ? (
                            <div className="movie-list">
                                {movies.map((movie) => (
                                    <div className="movie-item" key={movie.id}>
                                        <h2>{movie.title}</h2>
                                        <p>{movie.description}</p>
                                        <button
                                            onClick={() => {
                                                setExpandedMovieId((prev) =>
                                                    prev === movie.id ? null : movie.id
                                                );
                                                loadPosts(movie.id);
                                            }}
                                        >
                                            {expandedMovieId === movie.id
                                                ? 'Hide Details'
                                                : 'Show Posts'}
                                        </button>
                                        {expandedMovieId === movie.id && posts[movie.id] && (
                                            <div className="posts">
                                                <h3>Posts:</h3>
                                                <form
                                                    onSubmit={(e) => handleCreatePost(movie.id, e)}
                                                >
                                                    <input
                                                        type="text"
                                                        placeholder="Post Title"
                                                        value={newPost.title}
                                                        onChange={(e) =>
                                                            setNewPost({
                                                                ...newPost,
                                                                title: e.target.value,
                                                            })
                                                        }
                                                    />
                                                    <textarea
                                                        placeholder="Post Body"
                                                        value={newPost.body}
                                                        onChange={(e) =>
                                                            setNewPost({
                                                                ...newPost,
                                                                body: e.target.value,
                                                            })
                                                        }
                                                    ></textarea>
                                                    <button type="submit">Add Post</button>
                                                </form>
                                                {posts[movie.id].map((post) => (
                                                    <div className="post-item" key={post.id}>
                                                        <h4>{post.title}</h4>
                                                        <p>{post.body}</p>
                                                        <button
                                                            onClick={() =>
                                                                loadComments(movie.id, post.id)
                                                            }
                                                        >
                                                            Show Comments
                                                        </button>
                                                        {comments[post.id] && (
                                                            <div className="comments">
                                                                <h5>Comments:</h5>
                                                                <form
                                                                    onSubmit={(e) =>
                                                                        handleCreateComment(
                                                                            movie.id,
                                                                            post.id,
                                                                            e
                                                                        )
                                                                    }
                                                                >
                                                                    <input
                                                                        type="text"
                                                                        placeholder="Comment Content"
                                                                        value={newComment.content}
                                                                        onChange={(e) =>
                                                                            setNewComment({
                                                                                ...newComment,
                                                                                content: e.target.value,
                                                                            })
                                                                        }
                                                                    />
                                                                    <button type="submit">
                                                                        Add Comment
                                                                    </button>
                                                                </form>
                                                                {comments[post.id].map((comment) => (
                                                                    <p key={comment.id}>
                                                                        {comment.content}
                                                                    </p>
                                                                ))}
                                                            </div>
                                                        )}
                                                    </div>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p>Loading movies...</p>
                        )}
                    </div>
                </>
            )}
            <Footer />
        </div>
    );
};

export default App;
