import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import './App.css';
import Header from './Components/Header';
import Footer from './Components/Footer';
import Modal from './Components/Modal';
import { Route, Routes, useNavigate } from 'react-router-dom';
import Login from './Pages/Login';

const axiosInstance = axios.create({
    baseURL: 'https://localhost:5133/api',
    headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
        'Content-Type': 'application/json',
    }
});

const App = () => {
    const [movies, setMovies] = useState([]);
    const [expandedMovieId, setExpandedMovieId] = useState(null);
    const [modalVisible, setModalVisible] = useState(false);
    const [posts, setPosts] = useState({});
    const [comments, setComments] = useState({});

    const navigate = useNavigate();
    const isLoggedIn = localStorage.getItem('accessToken') !== null;

    const movieTitleRef = useRef(null);
    const movieDescriptionRef = useRef(null);
    const postTitleRef = useRef(null);
    const postBodyRef = useRef(null);
    const commentContentRef = useRef(null);

    // Extract userId from the token
    const userID = isLoggedIn ? jwtDecode(localStorage.getItem('accessToken')).sub : null;

    useEffect(() => {
        const loadMovies = async () => {
            try {
                const response = await axiosInstance.get('/movies');
                setMovies(response.data);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        if (isLoggedIn) {
            loadMovies();
        }
    }, [isLoggedIn]);

    if (!isLoggedIn) {
        return <Login />;
    }

    const handleLogout = () => {
        localStorage.removeItem('accessToken');
        navigate('/login');
    };

    const MovieList = () => {
        const loadPosts = async (movieId) => {
            try {
                const response = await axiosInstance.get(`/movies/${movieId}/posts`);
                setPosts((prev) => ({ ...prev, [movieId]: response.data }));
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        const loadComments = async (movieId, postId) => {
            try {
                const response = await axiosInstance.get(`/movies/${movieId}/posts/${postId}/comments`);
                setComments((prev) => ({
                    ...prev,
                    [postId]: response.data,
                }));
            } catch (error) {
                console.error('Error fetching comments:', error);
            }
        };

        return (
            <div className="App">
                <Header />
                <div className="content">
                    <h1>Movies List</h1>
                    <button className="create-movie-btn" onClick={() => setModalVisible(true)}>
                        Create New Movie
                    </button>

                    {movies.length > 0 ? (
                        <div className="movie-list">
                            {movies.map((movie) => (
                                <div className="movie-item" key={movie.id}>
                                    <h2>{movie.title}</h2>
                                    <p>{movie.description}</p>
                                    <button
                                        onClick={() => {
                                            setExpandedMovieId((prev) =>
                                                prev === movie.id ? null : movie.id
                                            );
                                            loadPosts(movie.id);
                                        }}
                                    >
                                        {expandedMovieId === movie.id ? 'Hide Details' : 'Show Posts'}
                                    </button>
                                    {expandedMovieId === movie.id && posts[movie.id] && (
                                        <div className="posts">
                                            <h3>Posts:</h3>
                                            <form onSubmit={(e) => handleCreatePost(movie.id, e)}>
                                                <input
                                                    type="text"
                                                    ref={postTitleRef}
                                                    placeholder="Post Title"
                                                />
                                                <textarea
                                                    ref={postBodyRef}
                                                    placeholder="Post Body"
                                                />
                                                <button type="submit">Add Post</button>
                                            </form>
                                            {posts[movie.id].map((post) => (
                                                <div key={post.id}>
                                                    <h4>{post.title}</h4>
                                                    <p>{post.body}</p>
                                                    <button
                                                        onClick={() =>
                                                            loadComments(movie.id, post.id)
                                                        }
                                                    >
                                                        {comments[post.id] ? 'Hide Comments' : 'Show Comments'}
                                                    </button>
                                                    {comments[post.id] && (
                                                        <div className="comments">
                                                            {comments[post.id].map((comment) => (
                                                                <div key={comment.id}>
                                                                    <p>{comment.body}</p>
                                                                </div>
                                                            ))}
                                                        </div>
                                                    )}
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p>No movies available.</p>
                    )}
                </div>
                <Footer />
                {modalVisible && <Modal setModalVisible={setModalVisible} />}
            </div>
        );
    };

    return (
        <Routes>
            <Route path="/" element={<MovieList />} />
        </Routes>
    );
};

export default App;
