import './Login.css';  // Import the CSS file
import { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [registerUsername, setRegisterUsername] = useState('');
    const [registerPassword, setRegisterPassword] = useState('');
    const [registerEmail, setRegisterEmail] = useState('');  // New state for email
    const [registerMessage, setRegisterMessage] = useState('');
    const [loginMessage, setLoginMessage] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // Send the username and password in the request body
            const response = await axios.post('https://localhost:5133/api/login', { userName: username, password });

            // Store the access token in localStorage
            localStorage.setItem('accessToken', response.data.accessToken);

            // Set the login success message
            setLoginMessage('Login successful!');
        } catch (error) {
            console.error('Login error', error);
            setLoginMessage('Login failed. Please try again.');
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        try {
            // Send the registration data (including email) in the request body
            const response = await axios.post('https://localhost:5133/api/accounts', { 
                userName: registerUsername, 
                password: registerPassword,
                email: registerEmail   // Send email
            });

            // Set the registration success message
            setRegisterMessage('Registration successful! You can now log in.');
        } catch (error) {
            console.error('Registration error', error);
            setRegisterMessage('Registration failed. Please try again.');
        }
    };

    return (
        <div className="login-container">
            <h1>Film Analysis Lounge</h1> {/* Title for the project */}

            {/* Login Form */}
            <form className="login-form" onSubmit={handleLogin}>
                <h2>Login</h2>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Login</button>
                {loginMessage && <p className="message">{loginMessage}</p>}
            </form>

            {/* Register Form */}
            <form className="register-form" onSubmit={handleRegister}>
                <h2>Register</h2>
                <input
                    type="text"
                    placeholder="Username"
                    value={registerUsername}
                    onChange={(e) => setRegisterUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={registerPassword}
                    onChange={(e) => setRegisterPassword(e.target.value)}
                />
                <input
                    type="email"  // Email input field
                    placeholder="Email"
                    value={registerEmail}
                    onChange={(e) => setRegisterEmail(e.target.value)}
                />
                <button type="submit">Register</button>
                {registerMessage && <p className="message">{registerMessage}</p>}
            </form>
        </div>
    );
};

export default Login;
